mapper.insert:
INSERT INTO bas_atts (
groupId
,sourceId
,sourceCode
,fileName
,fileSize
,fileType
,fileUrl
,fileId
,creator
,createDate
,lastModifier
,lastModDate
,status
) VALUES (
#{groupId}
,#{sourceId}
,#{sourceCode}
,#{fileName}
,#{fileSize}
,#{fileType}
,#{fileUrl}
,#{fileId}
,#{creator}
,#{createDate}
,#{lastModifier}
,#{lastModDate}
,#{status}
)

    SELECT LAST_INSERT_ID() AS ID

mapper.update:
UPDATE bas_atts
groupId=#{groupId}, sourceId=#{sourceId}, sourceCode=#{sourceCode}, fileName=#{fileName}, fileSize=#{fileSize}, fileType=#{fileType}, fileUrl=#{fileUrl}, fileId=#{fileId}, creator=#{creator}, createDate=#{createDate}, lastModifier=#{lastModifier}, lastModDate=#{lastModDate}, status=#{status},
WHERE id = #{id}

update.foreach:
UPDATE bas_atts
groupId=#{item.groupId}, sourceId=#{item.sourceId}, sourceCode=#{item.sourceCode}, fileName=#{item.fileName}, fileSize=#{item.fileSize}, fileType=#{item.fileType}, fileUrl=#{item.fileUrl}, fileId=#{item.fileId}, creator=#{item.creator}, createDate=#{item.createDate}, lastModifier=#{item.lastModifier}, lastModDate=#{item.lastModDate}, status=#{item.status},
WHERE id = #{item.id}

