mapper.insert:
INSERT INTO bas_user (
openid
,unionid
,nickName
,avatarUrl
,gender
,city
,province
,country
,phoneNumber
,purePhoneNumber
,countryCode
,creator
,createDate
,lastModifier
,lastModDate
,status
) VALUES (
#{openid}
,#{unionid}
,#{nickName}
,#{avatarUrl}
,#{gender}
,#{city}
,#{province}
,#{country}
,#{phoneNumber}
,#{purePhoneNumber}
,#{countryCode}
,#{creator}
,#{createDate}
,#{lastModifier}
,#{lastModDate}
,#{status}
)

    SELECT LAST_INSERT_ID() AS ID

mapper.update:
UPDATE bas_user
openid=#{openid}, unionid=#{unionid}, nickName=#{nickName}, avatarUrl=#{avatarUrl}, gender=#{gender}, city=#{city}, province=#{province}, country=#{country}, phoneNumber=#{phoneNumber}, purePhoneNumber=#{purePhoneNumber}, countryCode=#{countryCode}, creator=#{creator}, createDate=#{createDate}, lastModifier=#{lastModifier}, lastModDate=#{lastModDate}, status=#{status},
WHERE id = #{id}

update.foreach:
UPDATE bas_user
openid=#{item.openid}, unionid=#{item.unionid}, nickName=#{item.nickName}, avatarUrl=#{item.avatarUrl}, gender=#{item.gender}, city=#{item.city}, province=#{item.province}, country=#{item.country}, phoneNumber=#{item.phoneNumber}, purePhoneNumber=#{item.purePhoneNumber}, countryCode=#{item.countryCode}, creator=#{item.creator}, createDate=#{item.createDate}, lastModifier=#{item.lastModifier}, lastModDate=#{item.lastModDate}, status=#{item.status},
WHERE id = #{item.id}

