mapper.insert:
INSERT INTO biz_message (
groupId
,sourceId
,sourceCode
,messageTitle
,messageContent
,creatorName
,creator
,createDate
,lastModifier
,lastModDate
,status
) VALUES (
#{groupId}
,#{sourceId}
,#{sourceCode}
,#{messageTitle}
,#{messageContent}
,#{creatorName}
,#{creator}
,#{createDate}
,#{lastModifier}
,#{lastModDate}
,#{status}
)

    SELECT LAST_INSERT_ID() AS ID

mapper.update:
UPDATE biz_message
groupId=#{groupId}, sourceId=#{sourceId}, sourceCode=#{sourceCode}, messageTitle=#{messageTitle}, messageContent=#{messageContent}, creatorName=#{creatorName}, creator=#{creator}, createDate=#{createDate}, lastModifier=#{lastModifier}, lastModDate=#{lastModDate}, status=#{status},
WHERE id = #{id}

update.foreach:
UPDATE biz_message
groupId=#{item.groupId}, sourceId=#{item.sourceId}, sourceCode=#{item.sourceCode}, messageTitle=#{item.messageTitle}, messageContent=#{item.messageContent}, creatorName=#{item.creatorName}, creator=#{item.creator}, createDate=#{item.createDate}, lastModifier=#{item.lastModifier}, lastModDate=#{item.lastModDate}, status=#{item.status},
WHERE id = #{item.id}

