mapper.insert:
INSERT INTO biz_enroll_user (
groupId
,enrollId
,userId
,nickName
,remark
,creator
,createDate
,lastModifier
,lastModDate
,status
) VALUES (
#{groupId}
,#{enrollId}
,#{userId}
,#{nickName}
,#{remark}
,#{creator}
,#{createDate}
,#{lastModifier}
,#{lastModDate}
,#{status}
)

    SELECT LAST_INSERT_ID() AS ID

mapper.update:
UPDATE biz_enroll_user
groupId=#{groupId}, enrollId=#{enrollId}, userId=#{userId}, nickName=#{nickName}, remark=#{remark}, creator=#{creator}, createDate=#{createDate}, lastModifier=#{lastModifier}, lastModDate=#{lastModDate}, status=#{status},
WHERE id = #{id}

