mapper.insert:
INSERT INTO group_user_rel (
groupId
,userId
,userType
,userIdentity
,userStatus
,nickName
,remark
,creator
,createDate
,lastModifier
,lastModDate
,status
) VALUES (
#{groupId}
,#{userId}
,#{userType}
,#{userIdentity}
,#{userStatus}
,#{nickName}
,#{remark}
,#{creator}
,#{createDate}
,#{lastModifier}
,#{lastModDate}
,#{status}
)

    SELECT LAST_INSERT_ID() AS ID

mapper.update:
UPDATE group_user_rel
groupId=#{groupId}, userId=#{userId}, userType=#{userType}, userIdentity=#{userIdentity}, userStatus=#{userStatus}, nickName=#{nickName}, remark=#{remark}, creator=#{creator}, createDate=#{createDate}, lastModifier=#{lastModifier}, lastModDate=#{lastModDate}, status=#{status},
WHERE id = #{id}

update.foreach:
UPDATE group_user_rel
groupId=#{item.groupId}, userId=#{item.userId}, userType=#{item.userType}, userIdentity=#{item.userIdentity}, userStatus=#{item.userStatus}, nickName=#{item.nickName}, remark=#{item.remark}, creator=#{item.creator}, createDate=#{item.createDate}, lastModifier=#{item.lastModifier}, lastModDate=#{item.lastModDate}, status=#{item.status},
WHERE id = #{item.id}

